package Demo;
import java.util.Scanner;
public class task_25_number_of_digits_in_number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int a = sc.nextInt();
		String s = Integer.toString(a);
		
		int i=0;
		while(i<s.length()) {
			
			
			i++;
		}

		System.out.print(i);
	}
}
	
  
